parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "shortDescription": {
      "type": "string",
      "description": "A concise summary of the historical site (max 60 characters)."
    },
    "introduction": {
      "type": "string",
      "description": "An introduction section that begins with <h2>Introduction</h2> and contains 60-80 words in English, formatted in HTML with <p> tags."
    },
    "mainStory": {
      "type": "string",
      "description": "The main story section that starts with <h2>Historic Highlights</h2> and includes 400-800 words of detailed historical and cultural content in English. The content should be formatted in HTML using paragraphs (<p>), bold (<b>), italic (<i>), and additional subsections for readability."
    },
    "academicBackground": {
      "type": "string",
      "description": "An academic background section that begins with <h2>Timeline & Context</h2> followed by <h4>Historical Timeline</h4>. It must include a <ul> list of key historical dates and events formatted as list items (<li>) with bolded periods, and contain 400-600 words in English, using HTML formatting."
    }
  },
  "required": [
    "shortDescription",
    "introduction",
    "mainStory",
    "academicBackground"
  ]
}

