// Hent inputdata fra n8n
const content = $input.first().json['Perplexity research'] || ""; // Fallback til tom streng, hvis content er undefined
const citations = $('Perplexity HTTP').first().json.citations || []; // Fallback til tomt array, hvis citations er undefined

// Funktion til at erstatte [1], [2] osv. med Markdown-links og generere citations-liste
function insertCitations(text, citationsArray) {
  let result = typeof text === "string" ? text : "";

  // I Markdown er \n\n korrekt for nye afsnit, s√• vi beholder dem
  // Hvis der er behov, kan vi fjerne ekstra \n for at rydde op
  result = result.replace(/\n\n+/g, '\n\n').replace(/\n+$/, ''); // Normaliser til max 2 \n og fjern trailing \n

  // Tjek om citationsArray er et array og ikke tomt
  if (!Array.isArray(citationsArray) || citationsArray.length === 0) {
    return result; // Returner u√¶ndret tekst, hvis der ikke er citations
  }

  // Erstatter alle forekomster af [n] med Markdown-links
  citationsArray.forEach((url, index) => {
    const citationNumber = index + 1;
    const placeholder = new RegExp(`\\[${citationNumber}\\]`, 'g');
    const link = `[\\[${citationNumber}\\]](${url})`;
    result = result.replace(placeholder, link);
  });

  // Generer citations-listen i Markdown
  let citationsList = "\n\n### Citations\n";
  citationsArray.forEach((url, index) => {
    const citationNumber = index + 1;
    citationsList += `- [${citationNumber}] [${url}](${url})\n`;
  });

  // Tilf√∏j citations-listen efter teksten
  result += citationsList;

  return result;
}

// Udf√∏r transformationen
const transformedContent = insertCitations(content, citations);

// Returner resultatet som JSON
return [{
  json: {
    content: transformedContent,
    citations: citations // Bevar citations-arrayet, hvis n√∏dvendigt
  }
}];
