// Get the first element from input data
const inputData = $input.all()[0];

// Extract the string containing the array
const text = inputData.json.text;

// Use regex to find the array part of the string
const arrayString = text.match(/\[.*?\]/)[0];

// Convert the string to an actual array
const array = JSON.parse(arrayString);

// Transform the array into the desired format
const transformedArray = array.map(tag => ({ tag }));

// Return each transformed object as a separate item
return transformedArray.map(item => ({ json: item }));

