return transformedArray.map(item => ({ json: item }));

const allItems = $input.all();
const periods = allItems[0].json.data;

// Sortér efter start_year (stigende)
periods.sort((a, b) => a.start_year - b.start_year);

// Map hver periode til et letlæseligt strengformat, fx "[id, start-end]"
const formattedPeriods = periods.map(period => {
  const endYear = period.end_year === null ? 'nuværende' : period.end_year;
  return `[${period.id}, ${period.start_year}-${endYear}]`;
});

// Her gemmer vi hele arrayet i feltet "Periods"
return [
  {
    json: {
      Periods: formattedPeriods
    }
  }
];

