// Get all event URLs from the previous node
const items = $input.all();

// Create output array with one item per URL
const output = [];

for (const item of items) {
  if (item.json.url) {
    output.push({
      json: {
        eventUrl: item.json.url
      }
    });
  }
}

return output;

// Expect: input item has { data: "<html...>", eventUrl?: "https://www.bol.pt/Comprar/Bilhetes/..." }
const raw = $input.item.json?.data;
const eventUrl = $input.item.json?.eventUrl || '';

if (!raw || typeof raw !== 'string') {
  return {
    event_name: '',
    date_time_local: '',
    date_time_iso: '',
    status: 'error',
    sold_out_bool: null,
    description: 'No HTML data available',
    event_url: eventUrl,
    image_url: '',
  };
}

const html = raw;

// --- helpers ---
const monthMap = { jan:1, fev:2, mar:3, abr:4, mai:5, jun:6, jul:7, ago:8, set:9, out:10, nov:11, dez:12 };

function decodeEntities(s='') {
  return s
    .replace(/&nbsp;/g,' ')
    .replace(/&amp;/g,'&')
    .replace(/&lt;/g,'<')
    .replace(/&gt;/g,'>')
    .replace(/&quot;/g,'"');
}

function tryParseJSON(str) {
  try { return JSON.parse(str); } catch { return null; }
}

// Absolut√©r en URL i forhold til eventUrl/dom√¶ne
function absolutizeUrl(u='', baseOrigin='') {
  if (!u) return '';
  const url = decodeEntities(u.trim());
  if (/^data:/i.test(url)) return '';                  // ignor√©r data-URIs
  if (/^https?:\/\//i.test(url)) return url;
  if (/^\/\//.test(url)) return 'https:' + url;
  const base = baseOrigin || (eventUrl.match(/^https?:\/\/[^/]+/i)?.[0]) || 'https://www.bol.pt';
  if (url.startsWith('/')) return base + url;
  // relative path uden leading slash ‚Äì fors√∏g at bruge eventUrl‚Äôs path som dir
  try {
    const baseUrl = new URL(eventUrl || base);
    const dir = baseUrl.href.replace(/[^/]*$/, ''); // fjern filnavn
    return new URL(url, dir).href;
  } catch {
    return base + (base.endsWith('/') ? '' : '/') + url;
  }
}

// Find "content" i en meta-/link-tag string
function getAttr(tag='', attr='content') {
  return tag.match(new RegExp(attr + '=["\']([^"\']+)["\']','i'))?.[1] || '';
}

// Find alle JSON-LD events p√• siden
const ldMatches = [...html.matchAll(/<script[^>]+type=["']application\/ld\+json["'][^>]*>([\s\S]*?)<\/script>/gi)]
  .map(m => tryParseJSON(m[1]))
  .filter(o => o && (o['@type'] === 'Event' || (Array.isArray(o['@graph']) && o['@graph'].some(g => g['@type']==='Event'))));

// V√¶lg bedste JSON-LD event:
// 1) url matcher eventUrl, ellers 2) f√∏rste objekt af type Event.
function pickEventFromLD() {
  for (const obj of ldMatches) {
    if (obj['@type'] === 'Event') {
      if (eventUrl && obj.url && obj.url.includes(eventUrl)) return obj;
    }
    if (Array.isArray(obj['@graph'])) {
      for (const g of obj['@graph']) {
        if (g['@type'] === 'Event' && eventUrl && g.url && g.url.includes(eventUrl)) return g;
      }
    }
  }
  for (const obj of ldMatches) {
    if (obj['@type'] === 'Event') return obj;
    if (Array.isArray(obj['@graph'])) {
      const e = obj['@graph'].find(g => g['@type']==='Event');
      if (e) return e;
    }
  }
  return null;
}

const ldEvent = pickEventFromLD();

// --------- IMAGE URL (NY) ---------
function imageFromLd(img) {
  if (!img) return '';
  if (typeof img === 'string') return img;
  if (Array.isArray(img)) {
    for (const it of img) {
      const u = imageFromLd(it);
      if (u) return u;
    }
    return '';
  }
  if (typeof img === 'object') {
    // JSON-LD ImageObject varianter
    return img.url || img.contentUrl || img.thumbnailUrl || '';
  }
  return '';
}

// Meta-scrape for billede (og:image, twitter:image, itemprop=image, link rel=image_src)
function imageFromMeta(doc) {
  const metas = [...doc.matchAll(/<meta[^>]+(?:property|name|itemprop)=["']([^"']+)["'][^>]*>/gi)];
  const wanted = [
    'og:image:secure_url','og:image','twitter:image:src','twitter:image','image',
  ];
  for (const m of metas) {
    const key = (m[1] || '').toLowerCase();
    if (wanted.includes(key)) {
      const content = getAttr(m[0], 'content');
      if (content) return content;
    }
  }
  const linkImage = doc.match(/<link[^>]+rel=["']image_src["'][^>]*>/i)?.[0];
  if (linkImage) {
    const href = getAttr(linkImage, 'href');
    if (href) return href;
  }
  return '';
}

// V√¶lg ‚Äúbedste‚Äù kandidat fra et img-tag‚Äôs srcset (st√∏rste bredde)
function pickFromSrcset(tag='') {
  const srcset = tag.match(/srcset=["']([^"']+)["']/i)?.[1];
  if (!srcset) return '';
  const parts = srcset.split(',').map(s => s.trim()).filter(Boolean);
  let best = '';
  let bestW = -1;
  for (const part of parts) {
    const m = part.match(/(\S+)\s+(\d+)(w|x)/i);
    if (m) {
      const url = m[1];
      const val = parseInt(m[2],10);
      const weight = m[3].toLowerCase() === 'w' ? val : (val * 1000); // prioriter "w"
      if (weight > bestW) { bestW = weight; best = url; }
    } else {
      // Kun URL ‚Äì tag sidste som ofte er st√∏rst
      best = part.split(/\s+/)[0];
    }
  }
  return best;
}

function isLikelyPoster(url='') {
  const u = url.toLowerCase();
  if (!/\.(jpg|jpeg|png|webp|gif)(\?|#|$)/i.test(u)) return false;
  // filtr√©r tydelige ikoner/sprites/logos
  if (/(icon|favicon|apple|sprite|logo|sparkling|mcafee|norton|trust|ads|facebook|twitter|instagram)/i.test(u)) return false;
  return true;
}

function imageFromImgTags(doc) {
  const imgs = [...doc.matchAll(/<img[^>]+>/gi)].map(m => m[0]);
  // pr√∏v srcset f√∏rst (ofte giver st√∏rste plakat)
  for (const tag of imgs) {
    const candidate = pickFromSrcset(tag) || getAttr(tag,'src');
    if (candidate && isLikelyPoster(candidate)) return candidate;
  }
  return '';
}

function pickImageUrl() {
  // 1) JSON-LD
  let u = imageFromLd(ldEvent?.image);
  if (u) return u;

  // 2) META
  u = imageFromMeta(html);
  if (u) return u;

  // 3) IMG tags
  u = imageFromImgTags(html);
  if (u) return u;

  return '';
}

const baseOrigin = (eventUrl.match(/^https?:\/\/[^/]+/i)?.[0]) || 'https://www.bol.pt';
const rawImageUrl = pickImageUrl();
const imageUrl = rawImageUrl ? absolutizeUrl(rawImageUrl, baseOrigin) : '';

// --- title / name ---
let eventName = '';
if (ldEvent?.name) {
  eventName = String(ldEvent.name).trim();
} else {
  const t = html.match(/<title[^>]*>([\s\S]*?)<\/title>/i)?.[1] || '';
  let cleaned = t.replace(/\s+/g,' ').trim();
  cleaned = cleaned.replace(/^BOL\s*-\s*/i,'').replace(/^Bilhetes\s*/i,'');
  const idx = cleaned.indexOf(' - ');
  eventName = (idx !== -1 ? cleaned.slice(0, idx) : cleaned).trim();
}

// --- description (detail-side har "Sinopse"; ellers tom) ---
let description = '';
{
  const sinopse = html.match(/Sinopse[\s\S]*?<\/h[2-6]>([\s\S]*?)(?=<h[2-6]|Pre√ßos|Localiza√ß√£o|Morada|Dura√ß√£o|Abertura|Promotor|Sess[a√£]o|$)/i);
  if (sinopse) {
    description = decodeEntities(
      sinopse[1].replace(/<[^>]+>/g,' ').replace(/\s{2,}/g,' ').trim()
    );
  }
}

// --- status (HTML er mere trov√¶rdigt end JSON-LD availability p√• BOL) ---
const hasLabelEsgotado = /label-esgotado/i.test(html);
const hasComprarButton = /class=['"][^"']*\b(botao\s+comprar|comprar)\b[^"']*['"]/i.test(html) || /\bComprar\b/i.test(html);
const hasBotaoEscondido = /botao-escondido/i.test(html);
let status = 'unknown';
let soldOutBool = null;

if (hasLabelEsgotado || hasBotaoEscondido || (/\bEsgotado\b/i.test(html) && !hasComprarButton)) {
  status = 'sold out';
  soldOutBool = true;
} else if (hasComprarButton && !/\bEsgotado\b/i.test(html)) {
  status = 'on sale';
  soldOutBool = false;
}

// --- date/time ---
let date_time_iso = '';
let date_time_local = '';

function pad2(n){ return String(n).padStart(2,'0'); }

if (ldEvent?.startDate) {
  // JSON-LD giver allerede ISO8601
  const d = new Date(ldEvent.startDate);
  if (!isNaN(d)) {
    date_time_iso = d.toISOString();
    date_time_local = `${d.getFullYear()}-${pad2(d.getMonth()+1)}-${pad2(d.getDate())} ${pad2(d.getHours())}:${pad2(d.getMinutes())}`;
  }
}

if (!date_time_iso) {
  // Fald tilbage: find "18 out 2025 21:30" eller "18 out 2025, 21:30"
  const m = html.match(/(\d{1,2})\s+([A-Za-z√Ä-√ø]{3,})\s+(\d{4}),?\s+(\d{1,2}):(\d{2})/i);
  if (m) {
    const day = parseInt(m[1],10);
    const mon = (m[2] || '').toLowerCase().slice(0,3);
    const year = parseInt(m[3],10);
    const hour = parseInt(m[4],10);
    const minute = parseInt(m[5],10);
    const month = monthMap[mon];
    if (month) {
      const d = new Date(year, month-1, day, hour, minute);
      if (!isNaN(d)) {
        date_time_iso = d.toISOString();
        date_time_local = `${year}-${pad2(month)}-${pad2(day)} ${pad2(hour)}:${pad2(minute)}`;
      }
    }
  }
}

// Output (single item ‚Äì denne node er Function Item)
return {
  event_name: eventName || '',
  date_time_local: date_time_local || '',
  date_time_iso: date_time_iso || '',
  status,
  sold_out_bool: soldOutBool,
  description: description || '',
  event_url: eventUrl || (ldEvent?.url || ''),
  image_url: imageUrl || '',
};

// Get the extracted links from the previous node
const items = $input.all();

// Base URL for making relative URLs absolute
const baseUrl = 'https://www.bol.pt';

// Set to store unique URLs
const uniqueUrls = new Set();

// Process each item
for (const item of items) {
  // Get the eventLinks array from the item
  const eventLinks = item.json.eventLinks || [];

  // Process each link in the array
  for (const link of eventLinks) {
    let url = link;

    if (url) {
      // Make absolute URL if relative
      if (url.startsWith('/')) {
        url = baseUrl + url;
      } else if (!url.startsWith('http')) {
        url = baseUrl + '/' + url;
      }

      // Remove query strings and fragments
      url = url.split('?')[0].split('#')[0];

      // Add to set (automatically deduplicates)
      uniqueUrls.add(url);
    }
  }
}

// Convert set to array and return as items
const outputItems = Array.from(uniqueUrls).map(url => ({
  json: { url }
}));

return outputItems;

// Obter todos os eventos do n√≥ anterior e ordenar
const items = $input.all();
const sortedItems = items.sort((a, b) => {
  const dateA = new Date(a.json.dato_tid_iso || 0);
  const dateB = new Date(b.json.dato_tid_iso || 0);
  return dateA - dateB;
});

// Escape HTML simples para output seguro
function escapeHtml(str) {
  return String(str ?? '')
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#039;');
}

// Construir HTML
let html = `<!DOCTYPE html>
<html lang="pt">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width,initial-scale=1" />
  <title>Eventos de Tavira</title>
  <style>
    /* Estilos base */
    * { box-sizing: border-box; }
    body {
      margin: 0;
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Arial, sans-serif;
      background-color: #f5f5f5;
      color: #222;
      line-height: 1.6;
    }
    .container {
      max-width: 1200px;
      margin: 20px auto;
      padding: 20px;
    }

    /* Header */
    .header {
      background-color: #ffffff;
      padding: 24px;
      border-radius: 12px;
      box-shadow: 0 2px 8px rgba(0,0,0,0.08);
      margin-bottom: 24px;
    }
    h1 {
      margin: 0 0 8px 0;
      color: #333;
      font-size: 28px;
      line-height: 1.3;
      border-bottom: 3px solid #4CAF50;
      padding-bottom: 12px;
    }
    p.meta {
      margin: 8px 0 0 0;
      color: #666;
      font-size: 15px;
    }

    /* Grid de cart√µes */
    .cards-grid {
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      gap: 24px;
      margin-top: 24px;
    }

    /* Estilo do cart√£o */
    .event-card {
      background-color: #ffffff;
      border-radius: 12px;
      box-shadow: 0 2px 8px rgba(0,0,0,0.08);
      overflow: hidden;
      transition: transform 0.2s ease, box-shadow 0.2s ease;
      border: 1px solid #e0e0e0;
      margin-bottom: 24px;
    }
    .event-card:hover {
      transform: translateY(-4px);
      box-shadow: 0 4px 16px rgba(0,0,0,0.12);
      border-color: #d0d0d0;
    }

    /* Imagem do evento - bevarer original st√∏rrelse */
    .card-image-wrapper {
      width: 100%;
      background-color: #f8f8f8;
      display: flex;
      align-items: center;
      justify-content: center;
      min-height: 280px;
      max-height: 400px;
    }
    .card-image {
      max-width: 100%;
      height: auto;
      max-height: 400px;
      display: block;
    }
    .card-image-placeholder {
      width: 100%;
      min-height: 280px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      display: flex;
      align-items: center;
      justify-content: center;
      color: #fff;
      font-size: 16px;
      font-weight: 500;
    }

    /* Conte√∫do do cart√£o */
    .card-content {
      padding: 20px;
      padding-bottom: 24px;
    }

    .card-title {
      margin: 0 0 12px 0;
      font-size: 20px;
      font-weight: 600;
      color: #333;
      line-height: 1.3;
    }

    .card-datetime {
      display: inline-block;
      background-color: #E8F5E9;
      color: #2E7D32;
      padding: 6px 12px;
      border-radius: 6px;
      font-size: 14px;
      font-weight: 500;
      margin-bottom: 16px;
    }

    .card-description {
      color: #555;
      font-size: 15px;
      line-height: 1.7;
      margin-bottom: 20px;
      white-space: pre-wrap;
      word-wrap: break-word;
    }

    /* Bot√£o de a√ß√£o */
    .card-action {
      display: inline-block;
      background-color: #4CAF50;
      color: #fff !important;
      padding: 12px 24px;
      border-radius: 8px;
      text-decoration: none;
      font-weight: 500;
      font-size: 15px;
      transition: background-color 0.2s ease;
    }
    .card-action:hover {
      background-color: #45a049;
      color: #fff !important;
    }

    /* Mobile: 1 coluna */
    @media only screen and (max-width: 768px) {
      .container { 
        padding: 12px;
      }
      .header {
        padding: 20px;
        margin-bottom: 20px;
      }
      h1 {
        font-size: 24px;
      }
      .cards-grid {
        grid-template-columns: 1fr;
        gap: 20px;
      }
      .card-image-wrapper {
        min-height: 240px;
        max-height: 500px;
      }
      .card-image {
        max-height: 500px;
      }
      .card-image-placeholder {
        min-height: 240px;
      }
      .card-content {
        padding: 16px;
      }
      .card-title {
        font-size: 18px;
      }
    }

    /* Tablet: ainda 1 coluna for bedre l√¶sbarhed */
    @media only screen and (min-width: 769px) and (max-width: 1024px) {
      .cards-grid {
        grid-template-columns: 1fr;
        gap: 20px;
      }
    }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>Eventos dispon√≠veis - ${new Date().toLocaleDateString('pt-PT')}</h1>
      <p class="meta">N√∫mero de eventos: ${sortedItems.length}</p>
    </div>

    <div class="cards-grid">
`;

for (const item of sortedItems) {
  const eventName = escapeHtml(item.json.event_navn || 'N/D');
  const dateTime = escapeHtml(item.json.dato || 'N/D');
  const eventUrl  = item.json.event_url || '#';
  const imageUrl  = item.json.image_url || '';
  const descRaw   = item.json.beskrivelse || 'Nenhuma descri√ß√£o dispon√≠vel.';
  const descFull  = escapeHtml(descRaw);

  const imageHtml = imageUrl
    ? `<div class="card-image-wrapper"><img src="${imageUrl}" alt="Imagem do evento ${eventName}" class="card-image" /></div>`
    : `<div class="card-image-placeholder">üìÖ Evento em Tavira</div>`;

  html += `
      <div class="event-card">
        ${imageHtml}
        <div class="card-content">
          <h2 class="card-title">${eventName}</h2>
          <div class="card-datetime">üìÖ ${dateTime}</div>
          <div class="card-description">${descFull}</div>
          <a href="${eventUrl}" target="_blank" rel="noopener" class="card-action" style="color: #fff !important;">Comprar bilhete ‚Üí</a>
        </div>
      </div>
  `;
}

html += `
    </div>
  </div>
</body>
</html>`;

// Retornar item √∫nico com conte√∫do HTML
return [
  {
    json: {
      html,
      subject: `Atualiza√ß√£o de Eventos de Tavira - ${new Date().toLocaleDateString('pt-PT')}`,
      eventCount: sortedItems.length,
    },
  },
];
