// Encode workflow data to base64 to prevent data pollution
const items = $input.all();

for (const item of items) {
  const originalWorkflow = item.json;

  item.json = {
    id: originalWorkflow.id,
    name: originalWorkflow.name,
    n8nWorkflowData: Buffer.from(JSON.stringify(originalWorkflow)).toString('base64')
  };
}

return items;

// Helper function to ensure stable JSON serialization for reliable comparison.
function sortKeysDeep(obj) {
  if (obj === null || typeof obj !== 'object') return obj;
  if (Array.isArray(obj)) return obj.map(sortKeysDeep);
  const out = {};
  Object.keys(obj).sort().forEach(k => { out[k] = sortKeysDeep(obj[k]); });
  return out;
}

const items = $input.all();
const WORKFLOWS_DIR = $node["Configuration"].json.repo.path;

for (const item of items) {
  const src = item.json || {};
  const flags = {
    fileExists: false,
    nameChanged: false,
    shouldCommit: false
  };
  // Initialize the context container
  item.json.context = {
    oldFile: { path: '', name: '' },
    newFile: { path: '', name: '' },
    operation: ''
  };
  const context = item.json.context;

  // 1. Determine if the file exists on GitHub.
  const hasGithub = typeof src.githubWorkflowData === 'string' && src.githubWorkflowData.length > 0;
  flags.fileExists = hasGithub;

  // 2. Extract the current workflow name from the N8N data.
  const currentName = src.name || '';
  context.newFile.name = currentName;

  // 3. Detect renames and set file paths.
  if (typeof src.filePath === 'string' && src.filePath.length > 0) {
    const parts = src.filePath.split('/');
    const filename = parts.pop() || '';
    const githubName = filename.endsWith('.json') ? filename.slice(0, -5) : filename;

    flags.nameChanged = githubName !== currentName;
    context.oldFile.path = src.filePath;
    context.oldFile.name = githubName;

    const dirPath = parts.join('/');
    context.newFile.path = `${dirPath}/${currentName}.json`;

  } else {
    flags.nameChanged = false;
    context.newFile.path = `${WORKFLOWS_DIR}/${currentName}.json`.replace(/\/+/g, '/');
  }

  // 4. Perform a stable comparison to see if a commit is needed.
  try {
    if (flags.fileExists) {
      const n8nJsonStr = Buffer.from(src.n8nWorkflowData, 'base64').toString('utf8');
      const githubJsonStr = Buffer.from(src.githubWorkflowData, 'base64').toString('utf8');
      const n8nObj = JSON.parse(n8nJsonStr);
      const githubObj = JSON.parse(githubJsonStr);
      const stableN8nStr = JSON.stringify(sortKeysDeep(n8nObj));
      const stableGithubStr = JSON.stringify(sortKeysDeep(githubObj));
      flags.shouldCommit = stableN8nStr !== stableGithubStr;
    } else {
      flags.shouldCommit = true; // New file, always commit.
    }
  } catch (e) {
    flags.shouldCommit = true;   // If parsing or comparison fails, better to commit.
  }

  // 5. Determine the final operation type.
  if (flags.nameChanged) {
    context.operation = 'rename';
  } else if (!flags.fileExists) {
    context.operation = 'create';
  } else if (flags.shouldCommit) {
    context.operation = 'update';
  } else {
    context.operation = 'skip';
  }
}

return items;

let items = $input.all();

for (let item of items) {
    try {
        const contentBase64 = item.json.content;
        const path = item.json.path;
        const sha = item.json.sha;

        // Decode and parse GitHub file content to extract name
        const content = Buffer.from(contentBase64, 'base64').toString('utf8');
        const workflow = JSON.parse(content);

        // Keep only the fields we need from GitHub side, store as base64
        item.json = {
            id: workflow.id,
            name: workflow.name,
            filePath: path,
            githubWorkflowData: contentBase64, // Store as base64 to match N8N side
            sha: sha
        };

    } catch (error) {
        // Non-JSON or invalid workflow file
        console.log(`Error parsing file ${item.json.path}: ${error.message}`);
        item.json = {
            id: null,
            name: null,
            filePath: item.json.path,
            error: error.message
        };
    }
}

return items;

