// Hent input-teksten fra feltet 'text'
const inputText = $input.item.json.text;
const outputItem = $input.item; // Genbrug input-item

// Nulstil outputfelter fra evt. tidligere kørsler
delete outputItem.json.extractedPhoneSection;
delete outputItem.json.invoiceNumber;
delete outputItem.json.errors; // Fjerner evt. gammel fejlstruktur

// Initialiser en fejl-liste
let errors = [];

// Tjek om inputText faktisk er en streng
if (typeof inputText !== 'string') {
  outputItem.json.error = "Input data (text field) is not a string.";
  return $input.item;
}

// --- 1. Uddrag Detaljeret Telefonsektion (Korrigeret Stop-betingelse IGEN) ---
try {
  // 1. Find "Detalhes de comunicações e consumos" (non-capturing)
  // 2. Find linjen "Red Infinity 910297650" (^)
  // 3. Fang alt herfra (gruppe 1) non-greedy ([\s\S]*?)
  // 4. Stop lige før sektionen "Como pagar a sua fatura" starter
  //    (Tillad evt. blanke linjer før: \n*)
  const phoneSectionRegexPattern = '(?:Detalhes de comunicações e consumos[\\s\\S]*?)(^Red Infinity 910297650[\\s\\S]*?)(?=\\n*Como pagar a sua fatura)';
  const phoneSectionRegex = new RegExp(phoneSectionRegexPattern, 'm');
  const phoneMatch = inputText.match(phoneSectionRegex);

  if (phoneMatch && phoneMatch[1]) {
    // Gem den fangede sektion (gruppe 1)
    outputItem.json.extractedPhoneSection = phoneMatch[1].trim();
  } else {
    // Fejl hvis sektionen stadig ikke kan findes med den nye logik
    errors.push("Kunne ikke finde den specifikke telefonsektion ('Detalhes de comunicações...' -> 'Red Infinity 910297650' -> 'Como pagar...').");
  }
} catch (e) {
    errors.push("Fejl under udtræk af telefonsektion: " + e.message);
}


// --- 2. Uddrag Fakturanummer (Bør stadig virke) ---
try {
  const invoiceRegexPattern = /Documento nº\s+(FT\s+\d+\/\d+)/;
  const invoiceMatch = inputText.match(invoiceRegexPattern);

  if (invoiceMatch && invoiceMatch[1]) {
    outputItem.json.invoiceNumber = invoiceMatch[1].trim();
  } else {
    errors.push("Kunne ikke finde fakturanummer (format: Documento nº FT xxx/xxxxxxxxx).");
  }
} catch (e) {
    errors.push("Fejl under udtræk af fakturanummer: " + e.message);
}


// --- Oprydning og Returnering ---
// Tilføj kun fejl-feltet HVIS der opstod fejl
if (errors.length > 0) {
  outputItem.json.errors = errors;
}

// Returner det opdaterede item
return outputItem;

// Hent input-teksten fra feltet 'text'
const inputText = $input.item.json.text;
const outputItem = $input.item; // Start with the input item

// Initialiser et objekt til at holde de udtrukne værdier og evt. fejl
outputItem.json.invoiceDetails = {
  invoiceNumber: null,
  issueDate: null, // Udstedelsesdato
  amount: null,
  errors: [] // Samler evt. fejl undervejs
};

// Tjek om inputText faktisk er en streng
if (typeof inputText !== 'string') {
  outputItem.json.invoiceDetails.errors.push("Input data (text field) is not a string.");
  return outputItem; // Stop hvis input ikke er tekst
}

// --- 1. Uddrag Fakturanummer (Korrigeret Regex) ---
// Leder efter "Nº Documento", skipper over linjeskift/andet tekst non-greedy ([\s\S]*?),
// og fanger linjen der starter med "FT ..." (^ i multiline mode).
const invoiceRegex = /Nº Documento[\s\S]*?^(FT\s+\d+\/\d+)/m;
const invoiceMatch = inputText.match(invoiceRegex);

if (invoiceMatch && invoiceMatch[1]) {
  outputItem.json.invoiceDetails.invoiceNumber = invoiceMatch[1].trim();
} else {
  outputItem.json.invoiceDetails.errors.push("Kunne ikke finde fakturanummer (format: linje efter 'Nº Documento' startende med FT xxx/xxxxxxxxx).");
}

// --- 2. Uddrag Udstedelsesdato (Virker - ingen ændring) ---
const dateRegex = /Data de emissão:\s+(\d{2}-\d{2}-\d{4})/;
const dateMatch = inputText.match(dateRegex);
if (dateMatch && dateMatch[1]) {
  outputItem.json.invoiceDetails.issueDate = dateMatch[1].trim();
} else {
  outputItem.json.invoiceDetails.errors.push("Kunne ikke finde udstedelsesdato (format: Data de emissão: DD-MM-YYYY).");
}

// --- 3. Uddrag Beløb (Korrigeret Regex) ---
// Leder efter "Montante:", skipper over linjeskift/andet tekst non-greedy ([\s\S]*?),
// og fanger den første linje derefter, der starter med "€ ..." (^ i multiline mode).
const amountRegex = /Montante:[\s\S]*?^(€\s*\d+,\d{2})/m;
const amountMatch = inputText.match(amountRegex);

if (amountMatch && amountMatch[1]) {
  outputItem.json.invoiceDetails.amount = amountMatch[1].replace(/\s/g, '').trim();
} else {
    // Fallback: Hvis "Montante:" ikke findes, prøv "Valor deste mês" (Korrigeret Regex)
    // Leder efter "Valor deste mês", skipper linjeskift/andet non-greedy, fanger "€..." linje (^ i multiline).
    const fallbackAmountRegex = /Valor deste mês[\s\S]*?^(€\s*\d+,\d{2})/m;
    const fallbackAmountMatch = inputText.match(fallbackAmountRegex);
     if (fallbackAmountMatch && fallbackAmountMatch[1]) {
        outputItem.json.invoiceDetails.amount = fallbackAmountMatch[1].replace(/\s/g, '').trim();
        // Optional: Tilføj note om at fallback blev brugt
        // if(!outputItem.json.invoiceDetails.notes) outputItem.json.invoiceDetails.notes = [];
        // outputItem.json.invoiceDetails.notes.push("Brugte fallback beløb fra 'Valor deste mês'.");
     } else {
       // Hvis både primær og fallback fejler
       outputItem.json.invoiceDetails.errors.push("Kunne ikke finde beløb (hverken efter 'Montante:' eller 'Valor deste mês').");
     }
}

// --- Oprydning og Returnering ---
// Fjern error-listen hvis der ikke opstod fejl
if (outputItem.json.invoiceDetails.errors.length === 0) {
  delete outputItem.json.invoiceDetails.errors;
}

// Returner det opdaterede item med det nye 'invoiceDetails' objekt
return outputItem;
