// Loop over input items
for (const item of $input.all()) {
  // Filter results where title contains 'castelo' (case-insensitive)
  item.json.results = item.json.results.filter(result => 
    result.title.toLowerCase().includes('castelo')
  );

  // Add new field to each remaining result
  item.json.results.forEach(result => {
    result.myNewField = 1;
  });
}

return $input.all();
