parameters.jsCode:
// Forventet input = fire parallelle arrayer
const ids    = items[0].json.module_id;
const titles = items[0].json.title;
const desc   = items[0].json.description;
const cont   = items[0].json.content;

let promptBlocks = [];

for (let i = 0; i < ids.length; i++) {
  const n = i + 1;                          // rækkefølge-nummeret
  promptBlocks.push(
`# ${n}. ${titles[i]}

module_id: ${ids[i]}

**Kort beskrivelse**  
${desc[i]}

**Detaljeret indhold**  
${cont[i]}
`);
}

const fullPrompt = promptBlocks.join('\n\n---\n\n');

return [
  {
    json: { prompt: fullPrompt }            // ét samlet item til LLM-noden
  }
];

