parameters.jsCode:
// Hent alle items fra den tidligere node "Translate Module1"
const sourceItems = $("Translate Quiz").all(); // <- ændringen

// Brug en Map til at sikre unikke options baseret på module_question_option_id
const uniqueOptions = new Map();

for (const item of sourceItems) {
  const questions = item.json?.output?.module_questions ?? [];

  for (const question of questions) {
    const options = question.options ?? [];
    for (const option of options) {
      // Brug module_question_option_id som unik nøgle
      if (!uniqueOptions.has(option.module_question_option_id)) {
        uniqueOptions.set(option.module_question_option_id, {
          module_question_option_id: option.module_question_option_id,
          option_text: option.option_text,
          module_question_id: question.module_question_id,
        });
      }
    }
  }
}

// Konverter Map til array format som n8n forventer
const allOptions = Array.from(uniqueOptions.values()).map(opt => ({ json: opt }));
return allOptions;

// Brug en Map til at sikre unikke questions baseret på module_question_id
const uniqueQuestions = new Map();

for (const item of $input.all()) {
  const questions = item.json.output.module_questions;

  for (const question of questions) {
    // Brug module_question_id som unik nøgle
    if (!uniqueQuestions.has(question.module_question_id)) {
      uniqueQuestions.set(question.module_question_id, {
        module_question_id: question.module_question_id,
        module_id: question.module_id,
        question_text: question.question_text,
        language_code: 'en' // eller hent fra webhook hvis nødvendigt
      });
    }
  }
}

// Konverter Map til array format som n8n forventer
const allQuestions = Array.from(uniqueQuestions.values()).map(q => ({
  json: q
}));

return allQuestions;

