// Input: items = array fra Postgres-noden
const byKey = new Map();

for (const item of items) {
  const r = item.json;
  const qid  = r.module_question_id;
  const lang = r.module_question_translation?.language_code ?? 'unknown';
  const key  = `${qid}::${lang}`;

  if (!byKey.has(key)) {
    byKey.set(key, {
      module_id: r.module_id,
      module_question_id: qid,
      language_code: lang,
      question_position: r.module_question?.position ?? null,
      question_text: r.module_question_translation?.question_text ?? null,
      options: [],
    });
  }

  // Udelad options uden oversættelse, hvis du vil:
  const option = {
    module_question_option_id: r.module_question_option_id,
    option_position: r.module_question_option?.position ?? null,
    is_correct: r.module_question_option?.is_correct ?? null,
    option_text: r.module_question_option_translation?.option_text ?? null,
    option_language_code: r.module_question_option_translation?.language_code ?? lang,
  };

  // Tilføj kun hvis der faktisk er tekst (valgfrit filter)
  if (option.option_text != null) {
    byKey.get(key).options.push(option);
  }
}

// Sortér options efter position (valgfrit)
const out = Array.from(byKey.values()).map(q => {
  q.options.sort((a, b) => (a.option_position ?? 0) - (b.option_position ?? 0));
  return { json: q };
});

return out;

