parameters.jsCode:
// Input data kommer ind som item
const inputData = $input.all()[0].json.output;

// Regex til at matche JSON mellem markdown kodeblokke
const jsonRegex = /```(?:json)?(?:\s*)\n([\s\S]*?)\n```/g;

// Find matchet
const match = jsonRegex.exec(inputData);

let cleanJson = {};

if (match && match[1]) {
  try {
    // Parse den ekstraherede JSON-streng
    cleanJson = JSON.parse(match[1]);
  } catch (error) {
    console.log('Error parsing JSON:', error);
  }
}

// Returner det rene JSON-objekt
return { json: cleanJson };

