BEGIN;
    -- Slet translations relateret til questions → options
    DELETE FROM module_question_option_translations
    WHERE module_question_option_id IN (
        SELECT id FROM module_question_options
        WHERE module_question_id IN (
            SELECT id FROM module_questions
            WHERE module_id IN (
                SELECT id FROM modules WHERE course_id = $1
            )
        )
    );

    -- Slet translations relateret til questions
    DELETE FROM module_question_translations
    WHERE module_question_id IN (
        SELECT id FROM module_questions
        WHERE module_id IN (
            SELECT id FROM modules WHERE course_id = $1
        )
    );

    -- Slet options
    DELETE FROM module_question_options
    WHERE module_question_id IN (
        SELECT id FROM module_questions
        WHERE module_id IN (
            SELECT id FROM modules WHERE course_id = $1
        )
    );

    -- Slet spørgsmål
    DELETE FROM module_questions
    WHERE module_id IN (
        SELECT id FROM modules WHERE course_id = $1
    );




    -- Slet lesson translations
    DELETE FROM lesson_translations
    WHERE lesson_id IN (
        SELECT id FROM lessons
        WHERE module_id IN (
            SELECT id FROM modules WHERE course_id = $1
        )
    );



    -- Slet lessons
    DELETE FROM lessons
    WHERE module_id IN (
        SELECT id FROM modules WHERE course_id = $1
    );

    -- Slet module translations
    DELETE FROM module_translations
    WHERE module_id IN (
        SELECT id FROM modules WHERE course_id = $1
    );

    -- Slet modules
    DELETE FROM modules
    WHERE course_id = $1;

    -- Slet course translations
    DELETE FROM course_translations
    WHERE course_id = $1;




    -- Endeligt: slet selve kurset
    --DELETE FROM courses
    --WHERE id = $1;
END ;

